<?php if( ! defined('BASEPATH')) exit('No direct script access allowed');
class SamarMayatEmailModel extends CI_Model
{

    function __construct()
    {
        parent::__construct();
    }



    public function insert( $data ){

        if ($this->db->insert('samarMayatEmail', $data) ) {
            return $this->db->insert_id();
        } 
        return false;
    }
    
    public function setNotification( $data ){

        if ($this->db->insert('samarMayatEmailNotification', $data) ) {
            return $this->db->insert_id();
        } 
        return false;
    }
  
    public function getEmailContent( ){
      
        $query = $this->db->query('SELECT * from samarMayatEmail ORDER BY id DESC LIMIT 1');
        
        $query->result();

        return $query->result();
    }
  
    public function getEmailNotification( ){
      
        $query = $this->db->query('SELECT * from samarMayatEmailNotification ORDER BY id DESC LIMIT 1');
        
        $query->result();

        return $query->result();
    } 
    public function getUsers( ){
      
        $query = $this->db->query("SELECT CONCAT( first_name, ' ' , last_name ) as name, user_id, IFNULL( (select id from samarMayatNotificationUsers where Find_in_set(u.user_id, users)  ORDER BY id DESC LIMIT 1 ), false ) as status from user as u ");
        $query->result();
        

        return $query->result();
    }   
   public function getSamarMayatEmailNotification( ){
      
        $query = $this->db->query('SELECT * from samarMayatEmailNotification ORDER BY id DESC LIMIT 1');
        
        $query->result();

        return $query->result();
    }
   public function getSamarMayatEmailContent( ){
      
        $query = $this->db->query('SELECT * from samarMayatEmail  where type = "mayat"  ORDER BY id DESC LIMIT 1');
        
        $query->result();

        return $query->result();
    }
   public function getSamarEmailContent( ){
      
        $query = $this->db->query('SELECT * from samarMayatEmail where type = "samar"  ORDER BY id DESC LIMIT 1 ');
        
        $query->result();

        return $query->result();
    }  
  
     public function getUsersEmailsForNotification( ){
      
        $query = $this->db->query('select email from user where find_in_set ( user_id, (SELECT users from samarMayatNotificationUsers ORDER BY id DESC LIMIT 1) ) ');
        
        $query->result();

        return $query->result();
    }
  
  
    /*
    * Get record from table using single condition
    */
    function getrecordById( $tableName, $whereParam  ) {

        $this->db->select('*');
        $this->db->from($tableName);
        $this->db->where('id', $whereParam );
        $quary_result=$this->db->get();
        $result=$quary_result->row();
        
        return $result;
    }  
